declare module '*.svg' {
  const content: any;
  export default content;
}

declare module '*.svg?url' {
  const content: string;
  export default content;
}

declare module '*.png' {
  const content: string;
  export default content;
}

declare module '*.yml' {
  const content: any;
  export default content;
}

declare module '*.yaml' {
  const content: any;
  export default content;
}
